import env from 'src/env';
import { message } from 'antd';
export function beforeAvatarUpload(file) {
  var ext = file.name.split('.').pop().toLowerCase();
  var isImageAccept = env.imageAccept.split(',').map(function (item) {
    return item.trim();
  }).indexOf(".".concat(ext));

  if (isImageAccept === -1) {
    message.error("You can only upload ".concat(env.imageAccept, " file!"));
    return false;
  }

  var isLt2M = file.size / 1024 / 1024 < (env.maximumSizeUploadAvatar || 2);

  if (!isLt2M) {
    message.error("Image must smaller than ".concat(env.maximumSizeUploadAvatar || 2, "MB!"));
    return false;
  }

  return true;
}
