import cookie from 'js-cookie';
import { APIRequest, TOKEN, ROLE } from './api-request';
export class AuthService extends APIRequest {
  login(data) {
    return this.post('/auth/users/login', data);
  }

  setAuthHeader(token, role) {
    false && localStorage.setItem(TOKEN, token);
    false && localStorage.setItem(ROLE, role); // https://github.com/js-cookie/js-cookie
    // since Safari does not support, need a better solution

    cookie.set(TOKEN, token);
    cookie.set(ROLE, role);
    this.setAuthHeaderToken(token);
  }

  performerLogin(data) {
    return this.post('/auth/performers/login', data);
  }

  studioLogin(data) {
    return this.post('/auth/studio/login', data);
  }

  setToken(token) {
    false && localStorage.setItem(TOKEN, token); // https://github.com/js-cookie/js-cookie
    // since Safari does not support, need a better solution

    cookie.set(TOKEN, token);
    this.setAuthHeaderToken(token);
  }

  getToken() {
    const token = cookie.get(TOKEN);

    if (token) {
      return token;
    }

    return !token && false ? localStorage.getItem(TOKEN) : null;
  }

  getRole() {
    const role = cookie.get(ROLE);

    if (role) {
      return role;
    }

    return !role && false ? localStorage.getItem(ROLE) : null;
  }

  removeToken() {
    cookie.remove(TOKEN);
    cookie.remove(ROLE);
    false && localStorage.removeItem(TOKEN);
    false && localStorage.removeItem(ROLE);
  }

  removeRemember() {
    false && localStorage.removeItem('rememberMe');
  }

  updatePassword(body) {
    return this.put('/auth/users/me/password', body);
  }

  performersRegister(data) {
    return this.register('/auth/performers/register', data);
  }

  userRegister(data) {
    return this.post('/auth/users/register', data);
  }

  studioRegister(data) {
    return this.register('/auth/studio/register', data);
  }

  forgotPassword(email, type) {
    const data = {
      email,
      type
    };
    return this.post('/auth/users/forgot', data);
  }

  resendVerificationEmail(data) {
    return this.post('/verification/resend-verification-email', data);
  }

}
export const authService = new AuthService();
